INSERT INTO {pref}_{mod} VALUES (NULL, 5, 1, {time}, 0, 0, 'apple-inc', 'Компания Эппл Инк.', 'Компания Эппл Инк.', 'Компания Apple Inc. - Apple Computer Incorporated', 'Компания Apple Inc. - Apple Computer Incorporated', 'Apple, Computer, Компания, Эппл', '<p>Apple Computer Incorporated (Эппл Компьютер Инкорпорейтед); американская корпорация по разработке и производству персональных компьютеров, портативных музыкальных проигрывателей, а с недавних пор еще и мобильных телефонов, ориентированных на массового пользователя и отличающихся экономичностью и простотой использования.</p>', '<p>Сегодня компания Apple — живая легенда компьютерного мира. Основанная Стивом Джобсом и Стивом Возняком, как обычно, в гараже, она всего за несколько лет совершила, не побоюсь этого слова, компьютерную революцию. Пережив тяжелые годы, практически банкротство, сегодня компания возродилась, некогда уволенный Джобс вернулся к рулю и теперь дела у Apple идут совсем неплохо, она стала лидером во многих нишах и считается одной из самых инновационных компаний. Штаб-квартира находится в Купертино (Калифорния).</p>', '', '', 'up/firms/image/apple.jpg', 'up/firms/image/apple_thumb.jpg', 'left', 'Apple Computer Incorporated', '', 'Соединённые Штаты Америки', 'Калифорния2', 'Купертино', '1 Инфинит Луп', 'tester', 'Apple Computer', '+7 (800) 333-51-73', 'http://www.apple.com', 'media.ru@apple.com', 'Skype_apple', 0, 3, 3, '{\"1\":{\"path\":\"up/firms/files/power.rar\",\"title\":\"Тестовый файл\"}}', 'all', '', 'all', '', 0, 'yes', 0, 0, 0, '1,5', '{\"1\":{\"link\":\"http://vk.com/appleinc\",\"title\":\"Вконтакте\"},\"2\":{\"link\":\"https://www.facebook.com\",\"title\":\"Facebook\"},\"3\":{\"link\":\"https://twitter.com/apple\",\"title\":\"Twitter\"}}', 1, 0, 0);
