INSERT INTO {pref}_{mod}_tag VALUES (NULL, 'development', 'Разработка', 'Разработка программного обеспечения, это род деятельности или процесс, направленный на создание и поддержание работоспособности, качества и надежности программного обеспечения, используя технологии, методологию и практики из информатики.', '', '', '', '', 90);
